	memcpy(x3, x1, ILEN);
	br_i15_zero(z2, _src_ec_ec_c25519_i15_cC255_P[0]);
	memcpy(x2, z2, ILEN);
	x2[1] = 19;
	memcpy(z3, x2, ILEN);

